member.example:
This sample shows how to unzip a gzipped file

           using System;
           using System.IO;

           using ICSharpCode.SharpZipLib.GZip;

           class MainClass
           {
               public static void Main(string[] args)
               {
                   Stream s = new GZipInputStream(File.OpenRead(args[0]));
                   FileStream fs = File.Create(Path.GetFileNameWithoutExtension(args[0]));
                   int size = 2048;
                   byte[] writeData = new byte[2048];
                   while (true) {
                       size = s.Read(writeData, 0, size);
                       if (size > 0) {
                           fs.Write(writeData, 0, size);
                       } else {
                           break;
                       }
                   }
                   s.Close();
               }
           }

This sample shows how to gzip a file

           using System;
           using System.IO;

           using ICSharpCode.SharpZipLib.GZip;

           class MainClass
           {
               public static void Main(string[] args)
               {
                   Stream s = new GZipOutputStream(File.Create(args[0] + ".gz"));
                   FileStream fs = File.OpenRead(args[0]);
                   byte[] writeData = new byte[fs.Length];
                   fs.Read(writeData, 0, (int)fs.Length);
                   s.Write(writeData, 0, writeData.Length);
                   s.Close();
               }
           }

example.code:
using System;
using System.Text;
using System.Collections;
using System.IO;

using ICSharpCode.SharpZipLib.Zip;

class MainClass
{
    static public void Main(string[] args)
    {
        ZipFile zFile = new ZipFile(args[0]);
        Console.WriteLine("Listing of : " + zFile.Name);
        Console.WriteLine("");
        Console.WriteLine("Raw Size    Size      Date     Time     Name");
        Console.WriteLine("--------  --------  --------  ------  ---------");
        foreach (ZipEntry e in zFile) {
            DateTime d = e.DateTime;
            Console.WriteLine("{0, -10}{1, -10}{2}  {3}   {4}", e.Size, e.CompressedSize,
                                                                d.ToString("dd-MM-yy"), d.ToString("t"),
                                                                e.Name);
        }
    }
}

member.example:
This sample shows how to read a zip file

           using System;
           using System.Text;
           using System.IO;

           using ICSharpCode.SharpZipLib.Zip;

           class MainClass
           {
               public static void Main(string[] args)
               {
                   ZipInputStream s = new ZipInputStream(File.OpenRead(args[0]));

                   ZipEntry theEntry;
                   while ((theEntry = s.GetNextEntry()) != null) {
                       int size = 2048;
                       byte[] data = new byte[2048];

                       Console.Write("Show contents (y/n) ?");
                       if (Console.ReadLine() == "y") {
                           while (true) {
                               size = s.Read(data, 0, data.Length);
                               if (size > 0) {
                                   Console.Write(new ASCIIEncoding().GetString(data, 0, size));
                               } else {
                                   break;
                               }
                           }
                       }
                   }
                   s.Close();
               }
           }

This sample shows how to create a zip file

           using System;
           using System.IO;

           using ICSharpCode.SharpZipLib.Zip;

           class MainClass
           {
               public static void Main(string[] args)
               {
                   string[] filenames = Directory.GetFiles(args[0]);

                   ZipOutputStream s = new ZipOutputStream(File.Create(args[1]));

                   s.SetLevel(5); // 0 - store only to 9 - means best compression

                   foreach (string file in filenames) {
                       FileStream fs = File.OpenRead(file);

                       byte[] buffer = new byte[fs.Length];
                       fs.Read(buffer, 0, buffer.Length);

                       ZipEntry entry = new ZipEntry(file);

                       s.PutNextEntry(entry);

                       s.Write(buffer, 0, buffer.Length);

                   }

                   s.Finish();
                   s.Close();
               }
           }

